import { useEffect, useState } from 'react'
import React from 'react'
import axios from 'axios';


export const Pdf = () => {
     const [title,setTitle]=useState();
    const [file,setFile]=useState();
    const [allpdf,SetAllPdf]= useState(null);
    const [pdfFile, setPdfFile] = useState(null);

    useEffect(() => {
        getPdf();
      }, []);
      const getPdf = async () => {
        const result = await axios.get("http://localhost:8080/get-files");
        console.log(result.data.data);
        SetAllPdf(result.data.data);
      };

      const submitFile = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("title", title);
        formData.append("file", file);
        console.log(title, file);

        const result = await axios.post(
          "http://localhost:8080/upload-files",
          formData,
          {
            headers: { "Content-Type": "multipart/form-data" },
          }
        );
        console.log(result);
        if (result.data.status == "ok") {
          alert("Uploaded Successfully!!!");
          getPdf();
        }
      };

      const showPdf = (pdf) => {
        setPdfFile(`http://localhost:8080/files/${pdf}`)
      };

  return (

    <div className='pdf'>
        <form action="" onSubmit={submitFile}>
        <h3>Upload Pdf </h3>
        <input type="text" className='pdf-name' placeholder='PDF Title'  required onChange={(e)=>setTitle(e.target.value)}/>
        <input type="file" className='pdf-pdf' accept='application/pdf' required  onChange={(e)=>setFile(e.target.files[0])}/>
        <button className="pdf-button" type='submit'> Submit</button>
        </form>
        <div className="uploadedFile">
            <h4>Uploades Pdf</h4>


            <div className="uploadedFile-con">
                <h6>Title</h6>
                <button>Show Pdf</button>
            </div>
            {allpdf === null
            ? ("")
            : (allpdf.map((data) => {
                return (
                  <div className="inner-div">
                    <h6>Title: {data.title}</h6>
                    <button
                      className="btn btn-primary"
                      onClick={() => showPdf(data.pdf)}
                    >
                      Show Pdf
                    </button>
                  </div>
                );
              }))}
        </div>
        <PdfComp pdfFile={pdfFile}/>
    </div>
  )
}
