import { useState } from 'react'
import React from 'react'
import axios from 'axios';


export const Pdf = () => {
     const [title,setTitle]=useState();
    const [file,setFile]=useState();

    const sumbitFile=async(e)=>{
        e.preventDefault();
        const formData = new FormData();
        formData.append("title",title);
        formData.append("file",file);
        console.log(title,file)

        const result = await axios.post('http://localhost:3000/upload-files',formData)



    }
  return (

    <div className='pdf'>
        <form action="" onSubmit={sumbitFile}>
        <h3>Upload Pdf </h3>
        <input type="text" className='pdf-name' placeholder='PDF Title'  required onChange={(e)=>setTitle(e.target.value)}/>
        <input type="file" className='pdf-pdf' accept='application/pdf' required  onChange={(e)=>setFile(e.target.files[0])}/>
        <button className="pdf-button" type='submit'> Submit</button>


        </form>

    </div>
  )
}
