import { useEffect, useState } from 'react'
import React from 'react'
import axios from 'axios';


export const Pdf = () => {
     const [title,setTitle]=useState();
    const [file,setFile]=useState();
    const [allpdf,SetAllPdf]= useState(null);

    useEffect(()=>{
        getPdf();
    },[])
    const getPdf=async ()=>{
        const result = await axios.get("http://localhost:3000/get-files")
        SetAllPdf(result.data.data)
    }

    const submitFile = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("title", title);
        formData.append("file", file);
        console.log(title, file);

        try {
            const result = await axios.post("http://localhost:3000/upload-files", formData, {
                headers: { "Content-Type": "multipart/form-data" }
            });
            console.log(result.data);
        } catch (error) {
            console.error("Error uploading file:", error);
        }
    };

  return (

    <div className='pdf'>
        <form action="" onSubmit={sumbitFile}>
        <h3>Upload Pdf </h3>
        <input type="text" className='pdf-name' placeholder='PDF Title'  required onChange={(e)=>setTitle(e.target.value)}/>
        <input type="file" className='pdf-pdf' accept='application/pdf' required  onChange={(e)=>setFile(e.target.files[0])}/>
        <button className="pdf-button" type='submit'> Submit</button>
        </form>
        <div className="uploadedFile">
            <h4>Uploades Pdf</h4>


            <div className="uploadedFile-con">
                <h6>Title</h6>
                <button>Show Pdf</button>
            </div>
            {allpdf.length === 0 ? ( <p>No PDFs uploaded yet.</p>) :
            (
                    allpdf.map((data) => (
                        <div className="uploadedFile-viewer" >
                            <h6>Title: {data.title}</h6>
                                <button>Show PDF</button>ÃŸ
                        </div>
                    ))
                )}


        </div>
    </div>
  )
}
